package com.tutorials;

public class WhileLoopOddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				int n = 0;
				while(n<=99) {
					n++;
					if(n%2 == 0)
						System.out.println(n + "this number is even ");
					else
						
						System.out.println(n + "this number is odd");
				}
				
	}
				

	

}
